import React from 'react';
import logo from './logo.svg';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
  useParams,
  useRouteMatch
} from 'react-router-dom'
import InstagramEmbed from 'react-instagram-embed';
import './App.css';

const catslist = [
  { id: 0, name: 'Pepper', isProtagonist: false, race: 'Chartreux', color: 'Gris' },
  { id: 1, name: 'Aladdin', isProtagonist: false, race: 'Batard', color: 'Roux TigrÃ©' },
  { id: 2, name: 'Mr Mittens', isProtagonist: true, race: 'Batard', color: 'Noir' },
  { id: 4, name: 'Felix', isProtagonist: false, race: 'Siamois', color: 'Beige et Marron' },
];

const instaPosts = [
  { id: 0, url: 'https://instagr.am/p/Zw9o4/' },
  { id: 1, url: 'https://instagr.am/p/Zw8o4/' },
];

class AjaxComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    fetch("http://jsonplaceholder.typicode.com/todos")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });
          console.log(result)
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      ).catch(console.log)
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return (
        <div class="column is-three-fifths is-offset-one-quarter catbox">
          <div class="cat">
            <div class="cat__body"></div>
            <div class="cat__body"></div>
            <div class="cat__tail"></div>
            <div class="cat__head"></div>
          </div>
        </div>
      );
    } else {
      return (
        <div>
          {items.map(item => (
            <div className="column is-three-fifths is-offset-one-quarter">
              <div class="box">
                <article class="media">
                  <div class="media-left">
                    <figure class="image is-64x64">
                      <img src="https://bulma.io/images/placeholders/128x128.png" alt="Image" />
                    </figure>
                  </div>
                  <div class="media-content">
                    <div class="content">
