import React from 'react';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
} from 'react-router-dom'
import * as instagram from './Instagram.js';
import * as cats from './Cats.js';
import './App.css';


console.log(instagram.add(1, 2));

class NewsApiComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    fetch("https://newsapi.org/v2/everything?q=fortnite&from=2019-11-12&sortBy=publishedAt&apiKey=3c7a7bde959b42aa954054ba2f493814")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });
          console.log(result)
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      ).catch(console.log)
  }
  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return (
        <div className="column is-three-fifths is-offset-one-quarter catbox">
          <div className="cat">
            <div className="cat__body"></div>
            <div className="cat__body"></div>
            <div className="cat__tail"></div>
            <div className="cat__head"></div>
          </div>
        </div>
      );
    } else {
      console.log(items);
      return (
        <div className="columns is-multiline is-mobile is-centered">

          {items.articles.map(item => (
            <div className="column is-6">
              <div className="box">
                <article className="media">
                  <div className="media-content">
                    <div className="content">
                      <a href={item.url}>
                        <strong className="mediaposts-title">{item.title}</strong> <small></small>
                      </a>
                      <p>{item.content}</p>
                    </div>
                    <nav className="level is-mobile">
                      <div className="level-left">
                        <a className="level-item" aria-label="reply">
                          <span className="icon is-small">
                            <i className="fas fa-reply" aria-hidden="true"></i>
                          </span>
                        </a>
                        <a className="level-item" aria-label="retweet">
                          <span className="icon is-small">
                            <i className="fas fa-retweet" aria-hidden="true"></i>
                          </span>
                        </a>
                        <a className="level-item" aria-label="like">
                          <span className="icon is-small">
                            <i className="fas fa-heart" aria-hidden="true"></i>
                          </span>
                        </a>
                      </div>
                    </nav>
                  </div>
                </article>
              </div>

            </div>
          ))}

        </div>
      );
    }
  }
}

class AjaxComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      error: null,
      isLoaded: false,
      items: []
    };
  }

  componentDidMount() {
    fetch("http://kame.tv/articleJson")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            items: result
          });
          console.log(result)
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      ).catch(console.log)
  }

  render() {
    const { error, isLoaded, items } = this.state;
    if (error) {
      return <div>Error: {error.message}</div>;
    } else if (!isLoaded) {
      return (
        <div className="column is-three-fifths is-offset-one-quarter catbox">
          <div className="cat">
            <div className="cat__body"></div>
            <div className="cat__body"></div>
            <div className="cat__tail"></div>
            <div className="cat__head"></div>
          </div>
        </div>
      );
    } else {
      return (
        <div className="columns is-multiline is-mobile is-centered">

          {items.map(item => (
            <div className="column is-6">
              <div className="box">
                <article className="media">
                  <div className="media-content">
                    <div className="content">
                      <Link to={`article/${item.slug}`}>
                        <img src={item.cover} className="mediapostimg" />
                        <strong className="mediaposts-title">{item.titre}</strong> <small></small>
                      </Link>
                    </div>
                    <nav className="level is-mobile">
                      <div className="level-left">
                        <a className="level-item" aria-label="reply">
                          <span className="icon is-small">
                            <i className="fas fa-reply" aria-hidden="true"></i>
                          </span>
                        </a>
                        <a className="level-item" aria-label="retweet">
                          <span className="icon is-small">
                            <i className="fas fa-retweet" aria-hidden="true"></i>
                          </span>
                        </a>
                        <a className="level-item" aria-label="like">
                          <span className="icon is-small">
                            <i className="fas fa-heart" aria-hidden="true"></i>
                          </span>
                        </a>
                      </div>
                    </nav>
                  </div>
                </article>
              </div>

            </div>
          ))}

        </div>
      );
    }
  }
}



const characters = [
  { id: 1, name: 'Hello', atck: '1', def: '1', spike: '1' },
  { id: 2, name: 'Helloggzgz', atck: '1', def: '1', spike: '1' },
];

class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = { date: new Date() };
  }
  componentDidMount() {
    this.timerID = setInterval(
      () => this.tick(),
      1000
    );
    document.addEventListener('DOMContentLoaded', () => {

      // Get all "navbar-burger" elements
      const $navbarBurgers = Array.prototype.slice.call(document.querySelectorAll('.navbar-burger'), 0);

      // Check if there are any navbar burgers
      if ($navbarBurgers.length > 0) {

        // Add a click event on each of them
        $navbarBurgers.forEach(el => {
          el.addEventListener('click', () => {

            // Get the target from the "data-target" attribute
            const target = el.dataset.target;
            const $target = document.getElementById(target);

            // Toggle the "is-active" class on both the "navbar-burger" and the "navbar-menu"
            el.classList.toggle('is-active');
            $target.classList.toggle('is-active');

          });
        });
      }

    });
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  tick() {
    this.setState({
      date: new Date()
    });
  }

  render() {
    return (
      <div>
        <h1>Bonjour, monde !</h1>
        <h2>Il est {this.state.date.toLocaleTimeString()}.</h2>
      </div>
    );
  }
}


class Nav extends React.Component {
  constructor() {
    super();
    this.handleClick = this.handleClick.bind(this);
    this.handleOutsideClick = this.handleOutsideClick.bind(this);
    this.state = {
      navmenuCollapsed: false
    };
  }

  handleClick() {
    if (!this.state.navmenuCollapsed) {

      document.addEventListener('click', this.handleOutsideClick, false);

    } else {

      var nav = document.getElementById('nav');
      nav.className = "navbar-burger burger";
      var navbarExampleTransparentExample = document.getElementById('navbarExampleTransparentExample');
      navbarExampleTransparentExample.className = "navbar-menu";

      document.removeEventListener('click', this.handleOutsideClick, false);
    }

    this.setState(prevState => ({
      navmenuCollapsed: !prevState.navmenuCollapsed,
    }));
  }

  handleOutsideClick(e) {
    this.handleClick();
  }

  render() {
    return (
      <nav className="navbar is-black">
        <div className="navbar-brand">
          <a className="navbar-item" href="/">
            <img src="http://kame.tv/kame-logo-ecrit-blanc.svg" width="112" height="28" />
          </a>
          <div id="nav" className="navbar-burger burger" onClick={this.handleClick} data-target="navbarExampleTransparentExample">
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>

        <div id="navbarExampleTransparentExample" className="navbar-menu">

          <div className="navbar-start">
            <a className="navbar-item" href="/">
              Home
